    RadTabStrip1.FindTab(ItemWithEqualsTextAndValue)
End Sub
Private Shared Function ItemWithEqualsTextAndValue(ByVal tab As RadTab) As Boolean
    If tab.Text = tab.Value Then
        Return True
    Else
        Return False
    End If
End Function

